import math


class Solution:
    def longestPalindrome(self, s: str) -> str:
        max_len_j = 0
        max_str_j = ""
        max_len_k = 0
        max_str_k = ""
        str_len = len(s)
        for start in range(str_len):
            i = start
            j = start
            k = start + 1
            while True:
                if i < 0:
                    break
                s_i = s[i]
                s_j = s[j] if j < str_len else None
                s_k = s[k] if k < str_len else None
                if s_i != s_j and s_i != s_k:
                    break
                if s_i == s_j:
                    tmp_len = j - i + 1
                    if tmp_len >= max_len_j:
                        max_len_j = tmp_len
                        max_str_j = s[i:j + 1]
                else:
                    #   将下标移出范围
                    j = str_len + 1
                if s_i == s_k:
                    tmp_len = k - i + 1
                    if tmp_len >= max_len_k:
                        max_len_k = tmp_len
                        max_str_k = s[i:k + 1]
                else:
                    k = str_len + 1
                i -= 1
                j += 1
                k += 1
        max_str = max_str_k if max_len_k > max_len_j else max_str_j
        return max_str


if __name__ == '__main__':
    #   测试用例
    short_s = "0101001"
    long_s = ""
    #   运行
    sulution = Solution()
    res = sulution.longestPalindrome(long_s)
    print(res)
